import React , {Component} from 'react'
import './design.css'
import {NavLink} from 'react-router-dom'
import axios from 'axios'
export default class SignUp extends Component{
    constructor(){
        super()
        this.state={
            name:'',
            email:'',
            pwd:'',
            repwd:'',
            loading:false,
            message:''
        }
    }
    dataChange(e){
        this.setState({
            [e.target.name]:e.target.value
        })
    }
    postData(e){
        e.preventDefault()

        let name = this.state.name
        let email = this.state.email
        let pwd = this.state.pwd
        let repwd = this.state.repwd

        this.setState({
            loading : true
        })

        const data = {
            name,
            email,
            pwd,
            repwd
        }
        axios.post('http://localhost:3000/people',data)
        .then(res => {
            console.log(res)
            this.setState({
                loading:false,
                message:res.data
            })
        })
        .catch(err=>{
            console.log(err)
            this.setState({
                loading:false
            })
        })
    }
    loadOrShowMsg
    render(){
        return(
        <div>
            <center className="container">
                <form onSubmit={this.postData.bind(this)}>
                    <div className="container">
                    <h1>Registration form </h1>
                    <p>Please fill all field to create an account.</p>

                    <input type="text" className="fields" placeholder="Enter name" value={this.state.name} name="name" required onChange={this.dataChange.bind(this)}/><br/>

                    <input type="text" className="fields" placeholder="Enter Email" value={this.state.email} name="email" required onChange={this.dataChange.bind(this)}/><br/>

                    <select className="fieldsForSelect" name="country">
                        <option value="" selected disabled hidden>Choose Role</option>
                        <option value="admin">Admin</option>
                        <option value="author">Author</option>
                        <option value="default">Default</option>
                    </select><br/>

                    <input type="password" className="fields" placeholder="Enter Password" name="psw" required onChange={this.dataChange.bind(this)}/><br/>

                    <input type="password" className="fields" placeholder="Repeat Password" name="psw-repeat" required onChange={this.dataChange.bind(this)}/><br/>

                    <center>
                        <input type="submit" className="registerbtn" name="Register" />
                        <input type="reset" className="registerbtn" name="Reset" />
                        <p>Already have an account? <NavLink to="/" style={{color:'white'}}>Sign in</NavLink>.</p>
                    </center>
                    </div>
                </form>
                {this.loadOrShowMsg()}
            </center>
        </div>
        )
    }
}
