import React , {Component} from 'react'
import './design.css'
import axios from 'axios'

export default class LogIn extends Component{
    constructor(){
        super()
        this.state = {
            person:[]
        }
    }
    checkData(){

        axios.get('http://localhost:3000/people')
        .then(res =>{
            this.setState({
                person: res.data
            })
        })
    }
    render(){
        return(
            <div className="container">
            <center>
                <h2>Welcome</h2>
                <form onSubmit={this.checkData.bind(this)}>
                    <div>
                        <input 
                            type="text" 
                            className="fields" 
                            placeholder="Enter Email" 
                            name="email" 
                            required 
                        /><br/>
                        <input 
                            type="password" 
                            className="fields" 
                            placeholder="Enter Password" 
                            name="psw" 
                            required 
                        /><br/>                
                        <center>
                            <input type="submit" value="LogIn" className="registerbtn" />  
                        </center>
                        <div><h3>{this.state.person}</h3></div>
                    </div>
                </form>
            </center>
        </div>
        )
    }
}
