import React , {Component} from 'react'
import './design.css'
import { BrowserRouter as Router, Route, Link } from 'react-router-dom'
import { link } from 'fs';
import SignUp from './SignUp'
import LogIn from './LogIn'


export default class MainBody extends Component{
    constructor(){
        super()
        this.state = {
            loading: true,
        }

    }

    btns(){
        if(this.state.loading){  
        return(
            <div className="btn">
                <button className="registerbtn" onClick={this.disableBtn.bind(this)}>LogIn</button>
                <button className="registerbtn" onClick={this.disableBtn.bind(this)}>SignUp</button>
            </div>
            )
        }
    }

    disableBtn(){
        this.setState({
            loading: false
        })
        var lg=<LogIn />
        document.write(lg)
    }
    render(){

        return(
            <div id="bgImg">
                {this.btns()}
                <Router>
                    {/* <Links /> */}
                    <Route exact path="/LogIn" component={LogIn} />
                    <Route exact path="/SignUp" component={SignUp} />
                </Router> 
            </div>
        )

    }
}
