import React , {Component} from 'react'
import './design.css'
import axios from 'axios'

export default class LogIn extends Component{
    constructor(){
        super()
        this.state = {
            person:[],
            loading: true
        }
    }
    getData(){
        axios.get('http://localhost:3000/people')
        .then(res =>{
            console.log("check data working")
            console.log(res.data)
            this.setState({
                loading: false,
                person: res.data
            })
        })
    }
    checkData(){
        if(this.state.loading){
            return(
                <p>Loading...</p>
            )
        }else{
            let gotData=this.state.person
            gotData.map()
        }
    }
    render(){
        return(
            <div className="container">
            <center>
                <h2>Welcome</h2>
                <form onSubmit={this.getData.bind(this)}>
                    <div>
                        <input 
                            type="text" 
                            className="fields" 
                            placeholder="Enter Email" 
                            name="email" 
                            // required 
                        /><br/>
                        <input 
                            type="password" 
                            className="fields" 
                            placeholder="Enter Password" 
                            name="psw" 
                            // required 
                        /><br/>                
                        <center>
                            <input type="submit" value="LogIn" className="registerbtn" />  
                        </center>
                    </div>
                </form>
            </center>
        </div>
        )
    }
}
